import React from 'react';
import './Home.css'; // Asegúrate de que el archivo CSS existe en el directorio src

function Home() {
  const text = "Lenguaje de Señas";

  return (
    <div className="container">
      {/* Sección del encabezado */}
      <header className="header">
        <img
          src="https://fondosmil.co/fondo/70482.jpg" // Reemplaza con la URL de tu imagen
          alt="Header"
          className="header-image"
        />
        <div className="animation-container">
          <h1 className="animated-text">
            {text.split('').map((char, index) => (
              <span key={index} style={{ '--i': index }}>{char}</span>
            ))}
          </h1>
        </div>
      </header>

      {/* Sección de las tarjetas */}
      <div className="cards-container">
        <Card title="Card 1" content="Contenido de la primera tarjeta." />
        <Card title="Card 2" content="Contenido de la segunda tarjeta." />
        <Card title="Card 3" content="Contenido de la tercera tarjeta." />
      </div>
    </div>
  );
}

function Card({ title, content }) {
  return (
    <div className="card">
      <h2>{title}</h2>
      <p>{content}</p>
    </div>
  );
}

export default Home;
