import React from 'react';
import './Home.css'; // Asegúrate de que el archivo CSS existe en el directorio src

function Home() {
  const text = "Lenguaje de Señas";

  return (
    <div className="container">
      {/* Sección del encabezado */}
      <header className="header">
        <img
          src="https://fondosmil.co/fondo/70482.jpg" // Reemplaza con la URL de tu imagen
          alt="Header"
          className="header-image"
        />
        <div className="animation-container">
          <h1 className="animated-text">
            {text.split('').map((char, index) => (
              <span key={index} style={{ '--i': index }}>{char}</span>
            ))}
          </h1>
        </div>
      </header>
    </div>
  );
}

export default Home;
