import React from 'react';
import Slider from 'react-slick';
import './Home.css'; // Asegúrate de que el archivo CSS existe en el directorio src

function Home() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="container">
      {/* Header Section */}
      <header className="header">
        <Slider {...settings} className="carousel">
          <div className="carousel-slide">
            <img
              src="https://prod-images.dacast.com/wp-content/uploads/2023/04/top-online-video-platforms.png" // Replace with actual image URL
              alt="Slide 1"
              className="carousel-image"
            />
          </div>
          {/* Puedes añadir más diapositivas aquí si lo deseas */}
        </Slider>
        <div className="animation-container">
          <h1 className="animated-text">Lenguaje de Señas</h1>
        </div>
      </header>
    </div>
  );
}

export default Home;
