import React, { useState } from 'react'; // Importar useState
import './Abouttable.css';
import PropTypes from 'prop-types';

// Componente Card
const AboutCard = ({
  Titulo,
  image,
  stack,
  descripcion,
  button
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDescription = () => {
    setIsOpen(!isOpen);
  };

  const style = {
    img: {
      maxWidth: "400px"
    }
  };

  return (
    <div className="card-item">
      <div className="card hoverable">
        <div className="card-image waves-effect waves-block waves-light">
          <img style={style.img} className="activator" src={image} alt={Titulo} />
        </div>
        <div className="card-content">
          <div>
            <StackIcons data={stack} />
          </div>
          <span className="card-title grey-text text-darken-4">
            {Titulo}
          </span>
          <p className={`card-description ${isOpen ? 'open' : ''}`}>{descripcion}</p>
          <button className="more-info" onClick={toggleDescription}>
            {button}
          </button>
        </div>
        {isOpen && (
          <div className="card-reveal">
            <span className="card-title grey-text text-darken-4">
              {Titulo}
              <button className="close-info" onClick={toggleDescription}>Ã—</button>
            </span>
            <p>{descripcion}</p>
          </div>
        )}
      </div>
    </div>
  );
};

AboutCard.propTypes = {
  Titulo: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
  stack: PropTypes.arrayOf(PropTypes.string).isRequired,
  descripcion: PropTypes.string.isRequired,
  button: PropTypes.string.isRequired,
};

// Datos adicionales para las tarjetas
const additionalData = [
    {
        Titulo: "Tutorial",
        descripcion:
          "Some sample text giving project elevator pitch and blah blah and other stuff and goals and dreams and rainbows and butterflies and sunshine. ",
        button: "More Info",
        image:
          "https://s3-us-west-2.amazonaws.com/s.cdpn.io/326643/Sample%20Logo.png",
        stack: ["A", "B", "C"],
      },
      {
        Titulo: "Actividad 1",
        stack: ["A", "B", "C"],
        descripcion:
          "Some sample text giving project elevator pitch and blah blah and other stuff and goals and dreams and rainbows and butterflies and sunshine. ",
        button: "More Info",
        image:
          "https://s3-us-west-2.amazonaws.com/s.cdpn.io/326643/sample%20logo%202.png",
      },
      {
        Titulo: "Actividad 2",
        stack: ["D", "E"],
        descripcion:
          "Some sample text giving project elevator pitch and blah blah and other stuff and goals and dreams and rainbows and butterflies and sunshine. ",
        button: "More Info",
        image:
          "https://s3-us-west-2.amazonaws.com/s.cdpn.io/326643/Sample%20Logo.png",
      },
      {
        Titulo: "Actividad 4",
        stack: ["F", "G"],
        descripcion:
          "Some sample text giving project elevator pitch and blah blah and other stuff and goals and dreams and rainbows and butterflies and sunshine. ",
        button: "More Info",
        image:
          "https://s3-us-west-2.amazonaws.com/s.cdpn.io/326643/Sample%20Logo.png",
      },
      {
        Titulo: "Actividad 5",
        stack: ["H", "I", "J"],
        descripcion:
          "Some sample text giving project elevator pitch and blah blah and other stuff and goals and dreams and rainbows and butterflies and sunshine. ",
        button: "More Info",
        image:
          "https://s3-us-west-2.amazonaws.com/s.cdpn.io/326643/Sample%20Logo.png",
      },
      {
        Titulo: "Actividad 6",
        stack: ["K", "M", "L"],
        descripcion:
          "Some sample text giving project elevator pitch and blah blah and other stuff and goals and dreams and rainbows and butterflies and sunshine. ",
        button: "More Info",
        image:
          "https://s3-us-west-2.amazonaws.com/s.cdpn.io/326643/Sample%20Logo.png",
      }
];

const App = () => {
  return (
    <div className="card-container">
      {additionalData.map((item, idx) => <AboutCard key={idx} {...item} />)}
    </div>
  );
};

export default App;
