import React from 'react';
import ReactDOM from 'react-dom';
import './headerabout.css';

class About extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeID: 0,
      wrapperStyle: {
        backgroundImage: `url('${props.data[0].img}')`
      },
      panelStyle: {
        backgroundColor: props.data[0].colour
      },
      buttonHover: false,
      buttonStyle: {
        color: '#ffffff'
      }
    };
  }

  _changeActive = (id) => {
    const { data } = this.props;
    this.setState({
      activeID: id,
      wrapperStyle: {
        backgroundImage: `url('${data[id].img}')`
      },
      panelStyle: {
        backgroundColor: data[id].colour
      }
    });
  };

  _buttonColour = () => {
    this.setState(prevState => ({
      buttonHover: !prevState.buttonHover,
      buttonStyle: {
        color: prevState.buttonHover ? '#ffffff' : this.props.data[this.state.activeID].colour
      }
    }));
  };

  render() {
    const { data } = this.props;
    const { activeID, wrapperStyle, panelStyle, buttonStyle } = this.state;

    return (
      <section className="wrapper" style={wrapperStyle}>
        <Selectors 
          data={data}
          activeID={activeID}
          _changeActive={this._changeActive}
        />
        <Panel 
          data={data[activeID]}
          panelStyle={panelStyle}
          buttonStyle={buttonStyle}
          _buttonColour={this._buttonColour}
        />
      </section>
    );
  }
}

const Panel = ({ data, panelStyle, buttonStyle, _buttonColour }) => (
  <aside className="panel" style={panelStyle}>
    <h2 className="panel-header">{data.header}</h2>
    <p className="panel-info">{data.body}</p>
    <button
      className="panel-button"
      style={buttonStyle}
      onMouseEnter={_buttonColour}
      onMouseLeave={_buttonColour}
    >
      Read More
    </button>
  </aside>
);

const Selectors = ({ data, activeID, _changeActive }) => (
  <div className="selectors">
    {data.map((item) => (
      <Selector 
        key={item.id}
        id={item.id}
        _changeActive={_changeActive}
        activeID={activeID}
      />
    ))}
  </div>
);

const Selector = ({ id, _changeActive, activeID }) => {
  const componentClass = `selector ${activeID === id ? 'active' : ''}`;
  return (
    <div className={componentClass} onClick={() => _changeActive(id)}></div>
  );
}

// Datos de ejemplo
const data = [
  {
    id: 0,
    header: 'Gluten-free Bicycle',
    body: 'Chillwave knausgaard chambray flannel tumblr, narwhal microdosing blog...',
    colour: '#242846',
    img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/735173/rvc1.jpg'
  },
  {
    id: 1,
    header: 'Post-ironic Disrupt',
    body: 'Swag biodiesel disrupt retro fashion, salvia food truck kitsch wolf DIY...',
    colour: '#ba9077',
    img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/735173/rvc2.jpg'
  },
  {
    id: 2,
    header: 'Lumber-Sexual Roof Party',
    body: 'Flexitarian 3 wolf moon cliche, migas scenester street art...',
    colour: '#1ABC9C',
    img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/735173/rvc3.jpg'
  },
  {
    id: 3,
    header: 'Vegan hoodie trust fund',
    body: 'Farm-to-table tousled try-hard, normcore ethical tilde iPhone...',
    colour: '#C0392B',
    img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/735173/rvc4.jpg'
  },
  {
    id: 4,
    header: 'cliche craft beer',
    body: 'Tote bag flannel normcore polaroid +1. Quinoa actually 90s sustainable...',
    colour: '#513B56',
    img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/735173/rvc5.jpg'
  }
];

ReactDOM.render(
  <React.StrictMode>
    <Aboutheader data={data} />
  </React.StrictMode>,
  document.getElementById('app') // Asegúrate de que el ID sea 'app' en tu HTML
);

export default About;
