import React from 'react';
import { BrowserRouter as Router, Route, Link, Routes, useLocation } from 'react-router-dom';
import './App.css';

// Hook para resaltar la pestaña activa
function useActiveLink(path) {
  const location = useLocation();
  return location.pathname === path ? 'active' : '';
}

// Componentes de las Páginas
function Home() {
  return <h2>Home</h2>;
}

function About() {
  return <h2>About</h2>;
}

function Contact() {
  return <h2>Contact</h2>;
}

function App() {
  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <nav>
            <ul className="tabs">
              <TabLink to="/" path="/">Home</TabLink>
              <TabLink to="/about" path="/about">About</TabLink>
              <TabLink to="/contact" path="/contact">Contact</TabLink>
            </ul>
          </nav>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </header>
      </div>
    </Router>
  );
}

// Componente TabLink para resaltar la pestaña activa
function TabLink({ to, path, children }) {
  const isActive = useActiveLink(path);
  return (
    <li>
      <Link to={to} className={isActive}>{children}</Link>
    </li>
  );
}

export default App;
