import React, { useState } from 'react'; // Importar useState
import './Abouttable.css';
import PropTypes from 'prop-types';

// Componente StackIcons
const StackIcons = ({ data }) => {
  if (!Array.isArray(data)) {
    console.error('Expected an array for stack data');
    return null;
  }

  const icons = data.map((item) => (
    <div key={item} className="icon">
      <span className={item}>
        <span />
      </span>
    </div>
  ));
  return <div>{icons}</div>;
};

StackIcons.propTypes = {
  data: PropTypes.array.isRequired,
};

StackIcons.defaultProps = {
  data: [],
};

// Componente Card
const AboutCard = ({
  data: { status, description, needs, project, image, stack, website, github }
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDescription = () => {
    setIsOpen(!isOpen);
  };

  const style = {
    img: {
      maxWidth: "400px"
    }
  };

  return (
    <div className="card-item">
      <div className="card hoverable">
        <div className="card-image waves-effect waves-block waves-light">
          <img style={style.img} className="activator" src={image} alt={project} />
        </div>
        <div className="card-content">
          <div>
            <StackIcons data={stack} />
          </div>
          <span className="card-title activator grey-text text-darken-4">
            {project}
            <button className="more-info" onClick={toggleDescription}>...</button>
          </span>
          <div>
            <span>
              <a href={website} target="_blank" rel="noopener noreferrer">Website</a>
            </span>
            <span>
              <a className="github" href={github} target="_blank" rel="noopener noreferrer">
                Github
              </a>
            </span>
          </div>
          <p className={`card-description ${isOpen ? 'open' : ''}`}>{description}</p>
        </div>
        {isOpen && (
          <div className="card-reveal">
            <span className="card-title grey-text text-darken-4">
              {project}
              <button className="close-info" onClick={toggleDescription}>Ã—</button>
            </span>
            <div>
              <span className="status">{status}</span>
            </div>
            <p>{description}</p>
            <p>
              <strong>Need:</strong>
            </p>
            <p>{needs}</p>
          </div>
        )}
      </div>
    </div>
  );
};

Card.propTypes = {
  data: PropTypes.shape({
    status: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    needs: PropTypes.string.isRequired,
    project: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
    stack: PropTypes.arrayOf(PropTypes.string).isRequired,
    website: PropTypes.string.isRequired,
    github: PropTypes.string.isRequired,
  }).isRequired,
};

export default AboutCard;
