import React, { useState } from 'react';
import './Abouttable.css';

// Componente Card
const Card = ({
  data: { status, description, needs, project, image, stack, website, github }
}) => {
  const [showMore, setShowMore] = useState(false);

  const style = {
    img: {
      maxWidth: "400px"
    }
  };

  return (
    <div className="card-item">
      <div className="card hoverable">
        <div className="card-image waves-effect waves-block waves-light">
          <img style={style.img} className="activator" src={image} alt={project} />
        </div>
        <div className="card-content">
          <div>
            <StackIcons data={stack} />
          </div>
          <span className="card-title activator grey-text text-darken-4">
            {project}
            <i className="material-icons right">more_vert</i>
          </span>
          <div>
            <span>
              <a href={website} target="_blank" rel="noopener noreferrer">Website</a>
            </span>
            <span>
              <a className="github" href={github} target="_blank" rel="noopener noreferrer">
                Github
              </a>
            </span>
          </div>
          <p className="card-description">
            {showMore ? description : `${description.substring(0, 100)}...`}
          </p>
          <button onClick={() => setShowMore(!showMore)}>
            {showMore ? 'Show Less' : 'Show More'}
          </button>
        </div>
        <div className="card-reveal">
          <span className="card-title grey-text text-darken-4">
            {project}
            <i className="material-icons right">close</i>
          </span>
          <div>
            <span className="status">{status}</span>
          </div>
          <p>{description}</p>
          <p>
            <strong>Need:</strong>
          </p>
          <p>{needs}</p>
        </div>
      </div>
    </div>
  );
};

// Componente StackIcons (definido aquÃ­ para referencia)
const StackIcons = ({ data }) => {
  const icons = data.map((item) => (
    <div key={item} className="icon">
      <span className={item}>
        <span />
      </span>
    </div>
  ));
  return <div>{icons}</div>;
};

export default Card;
